scene.script:
function randomColor(){
  return '#' + (0x100000 + ~~(Math.random() * 0xEFFFFF)).toString(16);
}

function changeOnClick(element){
  element.addEventListener("click", function(){
    element.color = randomColor();
  });
}

scene.addEventListener("ready", function(event) {
  changeOnClick(scene.getElementById("one"));
  changeOnClick(scene.getElementById("two"));
});
