model.script:
{
  tick : function(self){
    var t = new Date().getTime() / 10000.0

    self.setPosition(new THREE.Vector3(
      Math.sin(t * 11) * 100,
      0,
      Math.cos(t * 7) * 100
    ));

    self.setRotation(new THREE.Euler(0,t * 10,0));
  }
}

box.script:
{
  tick : function(self){
    var t = new Date().getTime() / 10000.0 - 500;

    self.setPosition(new THREE.Vector3(
      Math.sin(t * 13) * 100,
      10,
      Math.cos(t * 11) * 100
    ));
  }
}

