scene.script:
scene.addEventListener("ready", function(event) {
  setInterval(function(){
    var t = new Date().getTime() / 10000.0

    var v = new Vector(
      Math.sin(t * 11) * 10, 0, Math.cos(t * 7) * 10
    );

    scene.getElementById("my-box").position = v.setY(0.5);
    scene.getElementById("other-box").position = v.negate().setY(0.5);
  }, 1000 / 10);
});
